INSERT [dbo].[Tbl_Product] ([Id], [CategoryId], [ProductName], [IsDelete], [CreatedDate], [ModifiedDate], [Description], [ProductImage], [IsFeatured], [Quantity], [Price]) VALUES (1, 1, N'6.6" Smt Samsung Galaxy A22 5G 128 ГБ', 0, NULL, CAST(N'2023-01-09T17:34:37.173' AS DateTime), N'Расширьте границы доступного с 6.6-дюймовым экраном с V-образным вырезом. Благодаря технологии Full HD+ дисплей демонстрирует четкую и чистую картинку. Играйте или листайте, технология обновления экрана с частотой 90 Гц обеспечит плавный просмотр любого контента. Смотрите на мир под разным углом с тройной камерой.  Запечатлейте лучшие моменты с 48 Мп основной камерой. Расширьте угол обзора с сверхширокоугольной или размойте фон с помощью камеры с датчиком глубины. Аналогично человеческому глазу объектив сверхширокоугольной камеры 5 Мп обеспечивает угол обзора 115°, благодаря чему у вас легко получится передать всю красоту панорамных снимков.', N'6.6 См Samsung Galaxy A22.PNG', 1, 6, CAST(240.00 AS Decimal(18, 2)))
GO
INSERT [dbo].[Tbl_Product] ([Id], [CategoryId], [ProductName], [IsDelete], [CreatedDate], [ModifiedDate], [Description], [ProductImage], [IsFeatured], [Quantity], [Price]) VALUES (2, 1, N'6.28" Smt Xiaomi 12X 256 ГБ ', 0, CAST(N'2022-12-16T18:40:14.063' AS DateTime), NULL, N'Xiaomi 12X оборудован чипом Snapdragon 870 и 6,28-дюймовым дисплеем с разрешением 2400 × 1080 точек. Селфи-камера оснащена 32-Мп датчиком, а тройная тыльная камера состоит из сенсоров на 50, 13 и 5 млн пикселей. Батарея ёмкостью 4500 мА·ч поддерживает 67-Вт проводную зарядку.', N'6.28 Sm Xiaomi 12X.png', 1, 12, CAST(840.00 AS Decimal(18, 2)))
GO
INSERT [dbo].[Tbl_Product] ([Id], [CategoryId], [ProductName], [IsDelete], [CreatedDate], [ModifiedDate], [Description], [ProductImage], [IsFeatured], [Quantity], [Price]) VALUES (4, 1, N'6.67" Smt Xiaomi Redmi Note 10 Pro 128 ГБ', 0, CAST(N'2022-12-16T18:46:08.010' AS DateTime), NULL, N'Смартфон Xiaomi Redmi Note 10 Pro по своему функционалу может соревноваться с самыми топовыми устройствами. Благодаря объему встроенной памяти, составляющему 128 ГБ, в нем можно хранить огромное количество приложений и файлов. Восьмиядерный процессор, 8 ГБ оперативной памяти и графический ускоритель Adreno 618 дают возможность легко проходить самые тяжеловесные мобильные игры. Основная камера модели с разрешением 108+8+5+2 Мп позволяет делать безукоризненное фото для блогов и соцсетей. Рассматривать и редактировать их удобно на AMOLED-экране с разрешением 2400x1080 пикс. Его диагональ составляет 6.67 дюйма. Благодаря встроенному модулю NFC и поддержке бесконтактной технологии оплаты Google Pay у владельца смартфона Xiaomi Redmi Note 10 Pro владельцу не нужно носить с собой банковские карты.', N'6.67 См Xiaomi Redmi Note 10 Pro.PNG', 1, 6, CAST(370.00 AS Decimal(18, 2)))
GO
INSERT [dbo].[Tbl_Product] ([Id], [CategoryId], [ProductName], [IsDelete], [CreatedDate], [ModifiedDate], [Description], [ProductImage], [IsFeatured], [Quantity], [Price]) VALUES (5, 2, N'7" Tbl Alcatel TKEE MINI 2 9317G 32 ГБ', 0, CAST(N'2022-12-16T18:51:14.093' AS DateTime), NULL, N'Детский планшет Alcatel TKEE MINI 2 9317G – это идеальное решение для знакомства ребёнка с миром технологий. Планшет, надёжный корпус которого выдержит удары и падения, создан стать верным помощником для безопасного и весёлого обучения ребенка с помощью игр. Режим “Защита глаз” заботится о зрении, уменьшая воздействие синего света на глаза. Функция “Родительский контроль” позволяет устанавливать ограничения и просматривать историю активностей. Образовательные приложения расширяют возможности для обучения с помощью игр.', N'7 пл Alcatel TKEE MINI.PNG', 1, 23, CAST(150.00 AS Decimal(18, 2)))
GO
