INSERT [dbo].[Tbl_Product] ([Id], [CategoryId], [ProductName], [IsDelete], [CreatedDate], [ModifiedDate], [Description], [ProductImage], [IsFeatured], [Quantity], [Price]) VALUES (2050, 2, N'8" Tab HUAWEI MatePad T 8 Kids 32 ГБ 3G, LTE', 0, NULL, CAST(N'2022-12-31T12:16:16.377' AS DateTime), N'Планшет оснащен 8-дюймовым IPS-дисплеем с разрешением 1280×800 пикселей. Модель получила характерный яркий дизайн в синем цвете, а в комплекте поставляется противоударный чехол, который изготовлен из пищевого силикона.
