INSERT [dbo].[Tbl_Product] ([Id], [CategoryId], [ProductName], [IsDelete], [CreatedDate], [ModifiedDate], [Description], [ProductImage], [IsFeatured], [Quantity], [Price]) VALUES (2126, 5, N'Трекбол Kensington Expert 64325', 0, NULL, CAST(N'2022-12-31T20:42:45.883' AS DateTime), N'Трекбол проводной Kensington Expert 64325 – отличная альтернатива для привычной компьютерной мыши. Некоторые пользователи предпочитают именно такой элемент для взаимодействия с устройством. Трекбол повышает точность позиционирования курсора, снимает нагрузку с запястья.
Модель Kensington Expert 64325 подключается по проводу. Шнур обладает оптимальной длиной 1.8 метра. Соединение с компьютером происходит через привычный разъем USB. Обеспечивается совместимость с различными стационарными ПК и ноутбуками.
Трекбол является универсальным. Им можно пользоваться как правой, так и левой рукой. Корпус выполнен из прочного глянцевого пластика. Цвет – черный, вам понравится лаконичный дизайн.
Общее количество кнопок – 4. Дополнительно предусмотрена возможность программирования элементов. Вы сможете настроить трекбол под себя для удобного использования.', N'Трекбол пр Kensington Expert 64325.PNG', 1, 9, CAST(110.00 AS Decimal(18, 2)))
GO
INSERT [dbo].[Tbl_Product] ([Id], [CategoryId], [ProductName], [IsDelete], [CreatedDate], [ModifiedDate], [Description], [ProductImage], [IsFeatured], [Quantity], [Price]) VALUES (2127, 5, N'Мышь A4Tech Bloody R8 SKULL', 0, NULL, CAST(N'2022-12-31T20:43:34.593' AS DateTime), N'Беспроводная мышь A4Tech Bloody R8 SKULL с ярким и стильным дизайном, эргономичным корпусом с подсветкой и мощным техническим оснащением станет настоящим оружием в руках геймера. Мышь оснащена функцией автоматического подавления отдачи, что обеспечивает максимальную четкость выстрелов. Заложенное программное обеспечение позволяет выбирать режим стрельбы одним нажатием кнопки. Внутренняя память надежно хранит настройки индивидуальных профилей.
Особенность A4Tech Bloody R8 SKULL заключается в минимальном времени отклика — всего 1 мс, такая скорость позволяет мгновенно реагировать на действия противников во время игры. Модель A4Tech Bloody R8 SKULL оснащена сенсором, максимальное разрешение которого составляет 3000 dpi. Общее количество кнопок — 8, одной из них можно переключать разрешение датчика. Подключение к компьютеру происходит через USB-ресивер, радиус действия которого составляет до 10 м.', N'Мышь бп A4Tech Bloody R8 SKULL.PNG', 1, 11, CAST(450.00 AS Decimal(18, 2)))
GO
INSERT [dbo].[Tbl_Product] ([Id], [CategoryId], [ProductName], [IsDelete], [CreatedDate], [ModifiedDate], [Description], [ProductImage], [IsFeatured], [Quantity], [Price]) VALUES (2128, 5, N'Мышь Aceline WM-905LU', 0, NULL, CAST(N'2022-12-31T20:44:11.097' AS DateTime), N'Компактная мышь беспроводная Aceline WM-905LU с красивой синей расцветкой дополнена покрытием SoftTouch, приятным на ощупь, матовым и простым в уходе. Универсальная конструкция позволяет настроить хват любой рукой. А разрешение датчика 1200 dpi гарантирует быстрый и четкий ответ на любые команды.
Устройство Aceline WM-905LU оснащено тремя кнопками управления, включая скролл (колесико). Прибор подключается к ПК с помощью USB-ресивера, действуя на расстоянии от него до 10 м. Мышь весит всего 60 г и обладает размерами 5.8x3.9x9.7 см.', N'Мышь бп Aceline WM-905LU.PNG', 1, 12, CAST(450.00 AS Decimal(18, 2)))
