INSERT [dbo].[Tbl_Product] ([Id], [CategoryId], [ProductName], [IsDelete], [CreatedDate], [ModifiedDate], [Description], [ProductImage], [IsFeatured], [Quantity], [Price]) VALUES (2129, 5, N'Мышь Razer Viper Ultimate Cyberpunk 2077', 0, NULL, CAST(N'2022-12-31T20:44:44.403' AS DateTime), N'Мышь беспроводная/проводная Razer Viper Ultimate Cyberpunk 2077 отличается ярким и узнаваемым дизайном и станет ярким украшением вашего рабочего места. Кроме того, данная модель отличается своей конструкцией, благодаря которой вы сможете комфортно играть и работать за компьютером.
Мышь Razer Viper Ultimate Cyberpunk 2077 предназначена для использования правой и левой рукой. Кроме того, за счет эргономичной формы, веса всего 74 г, а также качественного и приятного на ощупь пластика желтого цвета данная модель подарит вам комфорт при длительном использовании. Благодаря 8 кнопкам с возможностью программирования мышь сделает управление компьютером и игровым процессом особенно удобным. Помимо этого модель отличается высокой надежностью и обладает большим рабочим ресурсом кнопок.
Мышь Razer Viper Ultimate Cyberpunk 2077 использует в своей основе оптический светодиодный сенсор Razer Focus+, главной особенностью которого является высокая чувствительность – вплоть до 20000 точек на дюйм. ', N'Мышь бп пр Razer Viper Ultimate Cyberpunk 2077.PNG', 1, 5, CAST(210.00 AS Decimal(18, 2)))
