INSERT [dbo].[Tbl_Product] ([Id], [CategoryId], [ProductName], [IsDelete], [CreatedDate], [ModifiedDate], [Description], [ProductImage], [IsFeatured], [Quantity], [Price]) VALUES (2130, 5, N'Мышь Defender Hit MB-601', 0, NULL, CAST(N'2022-12-31T20:45:15.363' AS DateTime), N'Мышь проводная Defender Hit MB-601 имеет оригинальный дизайн. Особенностью модели является разноцветная подсветка. Этот конструктивный элемент позволит вам обнаружить мышь на рабочем столе даже в полной темноте.
В оснащение мыши Defender Hit MB-601 входят 4 кнопки. Оптический светодиодный датчик, максимальное разрешение которого составляет 1200 dpi, обеспечивает нужную точность позиционирования при использовании любых типовых программ. Вы также сможете использовать и другие режимы работы, а именно – 800 и 1000 dpi. Изменение разрешения осуществляется при помощи специальной кнопки.
Мышь Defender Hit MB-601 подключается к компьютеру проводным способом. Используемый интерфейс – USB. Длина интерфейсного кабеля равна 1.5 м. Вы будете в полной мере обеспечены свободой перемещений. Питание мыши осуществляется по шине.
Мышь имеет симметричный корпус. Это значит, что пользоваться устройством можно при помощи как правой, так и левой руки. Ширина, высота и длина манипулятора равны 68, 37 и 115 мм соответственно. Масса мыши – 110 г.', N'Мышь пр Defender Hit MB-601.PNG', 1, 33, CAST(5.00 AS Decimal(18, 2)))
