INSERT [dbo].[Tbl_Product] ([Id], [CategoryId], [ProductName], [IsDelete], [CreatedDate], [ModifiedDate], [Description], [ProductImage], [IsFeatured], [Quantity], [Price]) VALUES (2131, 5, N'Мышь DEXP СM-1201', 0, NULL, CAST(N'2022-12-31T20:46:23.147' AS DateTime), N'Мышь проводная Dexp СM-1201 представлена в черном корпусе с яркими желтыми вставками и обладает эргономичной конструкцией, что делает ее удобным приспособлением для пользования любой рукой. Разрешение светодиодного датчика соответствует 1200 dpi. Конструкция модели предусматривает 4 кнопки, среди которых особое место занимает кнопка переключения разрешения сенсора: возможна настройка на 800 dpi, 1000 dpi и 1200 dpi.
