GO
INSERT [dbo].[Tbl_Product] ([Id], [CategoryId], [ProductName], [IsDelete], [CreatedDate], [ModifiedDate], [Description], [ProductImage], [IsFeatured], [Quantity], [Price]) VALUES (2132, 5, N'Мышь Mad Catz R.A.T. PRO X3', 0, NULL, CAST(N'2022-12-31T20:47:30.177' AS DateTime), N'Мышь проводная Mad Catz R.A.T. PRO X3 – стильная игровая модель с оригинальным черным корпусом и яркой RGB-подсветкой. Полноценной эксплуатации способствует наличие десяти клавиш с возможностью программирования. В основе используется собственный сенсор PixArt PMW3389, обладающий предельным разрешением 16000 dpi.
Устройство Mad Catz R.A.T. PRO X3 оснащено прочным корпусом из металла и пластика, разработанным для удобного удержания правой рукой. Подключение к ПК осуществляется при помощи кабеля с плотной тканевой оплеткой и USB-разъемом. Габариты корпуса можно регулировать, а рабочий ресурс клавиш достигает 50 млн нажатий, что способствует длительному сроку службы.', N'Мышь пр Mad Catz R.A.T. PRO X3.PNG', 1, 9, CAST(220.00 AS Decimal(18, 2)))
