INSERT [dbo].[Tbl_Product] ([Id], [CategoryId], [ProductName], [IsDelete], [CreatedDate], [ModifiedDate], [Description], [ProductImage], [IsFeatured], [Quantity], [Price]) VALUES (2133, 5, N'Мышь Razer Naga Trinity', 0, NULL, CAST(N'2022-12-31T20:47:59.690' AS DateTime), N'Мышь проводная Razer Naga Trinity в черном корпусе с многоцветной подсветкой и стилизацией Razer является игровым аксессуаром, который позволит контролировать процесс игры. Это позволяет гарантировать ей преимущество в онлайн-играх, предусматривающих многопользовательский режим. Программируемые кнопки позволяют осуществлять настройку всего, что только можно– от конфигураций до вооружения, чтобы быть всегда впереди своего соперника. Всего модель насчитывает 19 кнопок.
Светодиодный датчик поддерживает настраиваемое разрешение 16000 dpi, поэтому позиционирование мыши всегда будет максимально точным. Razer Naga Trinity подключается посредством USB, используя кабель длиной 2.1 м. Мышь предусматривает наличие трех сменных панелей для достижения максимально комфортного пользования. Интерес представляет поддержка 5 игровых профилей, что позволит пользователю настроить мышь на применение в различных игровых приложениях. Резиновое покрытие с пластиком SoftTouch препятствует скольжению руки по корпусу.', N'Мышь пр Razer Naga Trinity.PNG', 1, 15, CAST(115.00 AS Decimal(18, 2)))
